    public double getTotal(String expensesId) {
		double total=0;
		TravelDetailExample e  = new TravelDetailExample();
		TravelDetailExample.Criteria c = e.createCriteria();
		if(CheckUtil.isEmptry(expensesId))
			return total;
		else
			c.andExpensesIdEqualTo(Long.parseLong(expensesId));
		
		List<TravelDetail> tlist = selectByExample(e);
		for (TravelDetail travelDetail : tlist) {
			total += travelDetail.getFee();
		}
		return total;
	}
